import React, { useState } from "react";

const categories = {
  스포츠: ["축구", "야구", "테니스", "탁구"],
  전략게임: ["체스", "장기", "바둑", "보드게임"],
  e스포츠: ["롤", "오버워치", "배그"],
};

const FilterBox = () => {
  const [selectedCategory, setSelectedCategory] = useState("스포츠");
  const [selectedSubCategories, setSelectedSubCategories] = useState([]);

  // 대분류 클릭 시 변경
  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSelectedSubCategories([]); // 대분류 바뀌면 소분류 초기화
  };

  // 소분류 체크 토글
  const handleSubCategoryToggle = (sub) => {
    if (selectedSubCategories.includes(sub)) {
      setSelectedSubCategories(selectedSubCategories.filter((s) => s !== sub));
    } else {
      setSelectedSubCategories([...selectedSubCategories, sub]);
    }
  };

  return (
    <div className="container my-4">
      <h5 className="mb-3">종목별 보기</h5>

      {/* 대분류 버튼 영역 */}
      <div className="d-flex gap-2 flex-wrap mb-3">
        {Object.keys(categories).map((cat) => (
          <button
            key={cat}
            className={`btn ${cat === selectedCategory ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => handleCategoryClick(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* 소분류 체크박스 영역 */}
      <div className="d-flex gap-3 flex-wrap">
        {categories[selectedCategory].map((sub) => (
          <div key={sub} className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              id={`check-${sub}`}
              checked={selectedSubCategories.includes(sub)}
              onChange={() => handleSubCategoryToggle(sub)}
            />
            <label className="form-check-label" htmlFor={`check-${sub}`}>
              {sub}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FilterBox;
